//
import { React, useState } from "react";
import Wrapper from "./Wrapper";

function Clientform() {
  let [firstname, setfirstname] = useState("");
  let [lastname, setlastname] = useState("");
  let [email, setemail] = useState("");
  let [phone, setphone] = useState("");
  let [Location, setLocation] = useState("");
  let [DateOfBirth, setDateOfBirth] = useState("");
  let [clientType, setClientType] = useState("Individual");

  const handleClient = (e) => {
    alert("called");
    const { name, value } = e.target;
    alert(value);
    setClientType(value);
  };

  const showDropDownMenuTwo_search = (el) => {
    el.target.parentElement.children[1].classList.toggle("hidden");
  };
  const swaptexttwo_search = (el) => {
    const targetText = el.target.innerText;
    document.getElementById("drop-down-content-setter-two_search").innerText =
      targetText;
    document
      .getElementById("drop-down-div-two_search")
      .classList.toggle("hidden");

    // const collectdata=async()=>{
    //   console.log(firstname,lastname,email,phone,location,DateOfBirth,Radio);

    // }
  };
  const collectdata = async () => {
    console.log(firstname, lastname, email, phone, Location, DateOfBirth);
    let info = await fetch("http://localhost:5600", {
      method: "post",
      body: JSON.stringify({
        firstname,
        lastname,
        email,
        phone,
        Location,
        DateOfBirth,
        clientType,
      }),
      headers: {
        "Content-Type": "application/json",
      },
    });
    info = await info.json();
    console.log(info);
  };

  return (
    <Wrapper>
      <div>
        <div className="w-full bg-white p-10">
          <div className="md:flex items-center border-b pb-6 border-gray-200">
            <div className="flex items-center md:mt-0 mt-4"></div>
          </div>
          <h1
            tabIndex={0}
            role="heading"
            aria-label="profile information"
            className="focus:outline-none text-3xl font-bold text-gray-800 mt-12"
          >
            New Client
          </h1>
          <p
            role="contentinfo"
            className=" focus:outline-nonetext-sm font-light leading-tight text-gray-600 mt-4"
          >
            Fill in the data for profile. It will take a couple of minutes.{" "}
            <br />
          </p>
          <h2
            role="heading"
            aria-label="enter Personal data"
            className="text-xl font-semibold leading-7 text-gray-800 mt-10"
          >
            Personal data
          </h2>

          <div className="mt-8 md:flex items-center">
            <div className="flex flex-col">
              <label className="mb-3 text-sm leading-none text-gray-800">
                First name
              </label>
              <input
                type="name"
                tabIndex={0}
                aria-label="Enter first name"
                className="w-64 bg-gray-100 text-sm font-medium leading-none text-gray-800 p-3 border rounded border-gray-200"
                defaultValue="William"
                value={firstname}
                onChange={(e) => {
                  setfirstname(e.target.value);
                }}
              />
            </div>
            <div className="flex flex-col md:ml-12 md:mt-0 mt-8">
              <label className="mb-3 text-sm leading-none text-gray-800">
                Last name
              </label>
              <input
                type="name"
                tabIndex={0}
                aria-label="Enter last name"
                className="w-64 bg-gray-100 text-sm font-medium leading-none text-gray-800 p-3 border rounded border-gray-200"
                defaultValue="Smith"
                value={lastname}
                onChange={(e) => {
                  setlastname(e.target.value);
                }}
              />
            </div>
          </div>
          <div className="mt-12 md:flex items-center">
            <div className="flex flex-col">
              <label className="mb-3 text-sm leading-none text-gray-800">
                Email Address
              </label>
              <input
                type="email"
                tabIndex={0}
                aria-label="Enter email Address"
                className="w-64 bg-gray-100 text-sm font-medium leading-none text-gray-800 p-3 border rounded border-gray-200"
                defaultValue="smith.william@gmail.com"
                value={email}
                onChange={(e) => {
                  setemail(e.target.value);
                }}
              />
            </div>
            <div className="flex flex-col md:ml-12 md:mt-0 mt-8">
              <label className="mb-3 text-sm leading-none text-gray-800">
                Phone number
              </label>
              <input
                type="number"
                tabIndex={0}
                aria-label="Enter phone number"
                className="w-64 bg-gray-100 text-sm font-medium leading-none text-gray-800 p-3 border rounded border-gray-200"
                defaultValue="+81 839274"
                value={phone}
                onChange={(e) => {
                  setphone(e.target.value);
                }}
              />
            </div>
          </div>
          <div className="mt-12 md:flex items-center">
            <div className="flex flex-col">
              <label className="mb-3 text-sm leading-none text-gray-800">
                Date of birth
              </label>
              <input
                type
                tabIndex={0}
                aria-label="Enter date of birth"
                className="w-64 bg-gray-100 text-sm font-medium leading-none text-gray-800 p-3 border rounded border-gray-200"
                defaultValue="28.03.1997"
                value={DateOfBirth}
                onChange={(e) => {
                  setDateOfBirth(e.target.value);
                }}
              />
            </div>
            <div className="flex flex-col md:ml-12 md:mt-0 mt-8">
              <label className="mb-3 text-sm leading-none text-gray-800">
                Location
              </label>
              <input
                type="name"
                tabIndex={0}
                aria-label="Enter place of birth"
                className="w-64 bg-gray-100 text-sm font-medium leading-none text-gray-800 p-3 border rounded border-gray-200"
                defaultValue="San Diego, CA, USA"
                value={Location}
                onChange={(e) => {
                  setLocation(e.target.value);
                }}
              />
            </div>
          </div>

          {/* radio button */}
          {/* <div className="mt-8" style={{ width: "25%" }}>
          <label className="text-sm text-left font-medium leading-none text-gray-800">
            Group Type
          </label>
          <div className="dropdown-one border  border-gray-300 w-full rounded outline-none   relative mt-2">
            <button
              onClick={showDropDownMenuTwo_search}
              className="dropbtn-one relative px-5 py-[12px] flex items-center justify-between w-full"
            >
              <span
                className="pr-4 font-medium text-gray-600 text-sm"
                id="drop-down-content-setter-two_search"
              >
                Individual
              </span>
              <svg
                className="absolute right-5 z-10 cursor-pointer"
                width={10}
                height={6}
                viewBox="0 0 10 6"
                fill="none"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  d="M0.5 0.75L5 5.25L9.5 0.75"
                  stroke="#4B5563"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                />
              </svg>
            </button>
            <div
              className="hidden rounded w-full shadow border-t border-gray-200 px-1 py-2 absolute top-12 right-0 bg-white z-10"
              id="drop-down-div-two_search"
              value={Radio}
            >
              <a href="javascript:void(0)" className="hover">
                <p
                  className="text-sm leading-none text-gray-600 p-3 hover:bg-indigo-100 hover:font-medium hover:text-indigo-700 hover:rounded cursor-pointer"
                  onClick={swaptexttwo_search}
                >
                  Individual
                </p>
              </a>
              <a href="javascript:void(0)">
                <p
                  className="text-sm leading-none text-gray-600 p-3 hover:bg-indigo-100 hover:font-medium hover:text-indigo-700 hover:rounded cursor-pointer"
